#title: Buurt;

PREFIX igeo: <http://rdf.insee.fr/def/geo#>
PREFIX xsd: <http://www.w3.org/2001/XMLSchema#>
PREFIX im: <http://imgpedia.dcc.uchile.cl/resource/>
prefix geo: <http://www.opengis.net/ont/geosparql#>
prefix rdfs: <http://www.w3.org/2000/01/rdf-schema#>
prefix imx:<http://modellen.geostandaarden.nl/def/imx-geo#>
prefix imx-ext: <https://modellen.kkg.kadaster.nl/def/imxgeo-ext#>


select ?uri ?geometry ?color ?label
#baselayer:PDOK grijs;
where
{
{
  <{{buurt}}> imx:bevatPerceel ?uri .     
  ?uri geo:hasGeometry/geo:asWKT ?geometry.
  ?uri imx-ext:perceelnummer ?label.

  bind ("Perceel" as ?color)

    }

        union
        {

   <{{buurt}}> imx:bevatPerceel/^imx:bevindtZichOpPerceel ?uri.
    bind (strbefore(strafter(str(?uri),"/id/"),"/")  as ?color2)
    optional {?uri imx:type ?color3}
    bind (coalesce(?color3,?color2) as ?color)

    {?uri imx-ext:maaiveldgeometrie/geo:asWKT ?geometrymv. 
   optional {
  ?uri imx-ext:bovenaanzichtgeometrie/geo:asWKT ?geometrybo }
    bind (coalesce (?geometrybo,?geometrymv) as ?geometry)
  }

    union {

  ?uri geo:hasGeometry/geo:asWKT ?geometry }
  }



}


