#noLeffgend;
#minimal;
#baselayer:PDOK grijs;
select distinct ?uri ?geometry ?label  (xsd:float(count (distinct ?gebouw))  as ?color)
where
{

  ?uri geo:sfWithin <{{gem}}>.
          ?uri a imx:Wijk.
          ?uri geo:hasGeometry/geo:asWKT ?geometry.

          ?uri imx:naam ?label.
