areas.query:
PREFIX geo: <http://www.opengis.net/ont/geosparql#>
PREFIX rdf: <http://www.w3.org/1999/02/22-rdf-syntax-ns#>
PREFIX rdfs: <http://www.w3.org/2000/01/rdf-schema#>

select ?type (count (distinct ?x) as ?aantal)

where
{
  {
   ?eigendom <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
  filter not exists 
  {
  	?eigendom2 <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    filter (?eigendom2!=?eigendom)
  }
  bind ("1 eigenaar" as ?type)
  }
  union
  {
   ?eigendom <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    ?eigendom2 <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    filter (?eigendom!=?eigendom2)
  filter not exists 
  {
  	?eigendom3 <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    filter (?eigendom3!=?eigendom)filter (?eigendom3!=?eigendom2)
  }
  bind ("2 eigenaren" as ?type)
  }
   union
  {
   ?eigendom <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    ?eigendom2 <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    	?eigendom3 <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    filter (?eigendom!=?eigendom2) filter (?eigendom3!=?eigendom)filter (?eigendom3!=?eigendom2)
  filter not exists 
  {
  	?eigendom4 <http://modellen.geostandaarden.nl/def/imx-brk#rustOp> ?x.
    filter (?eigendom4!=?eigendom)filter (?eigendom4!=?eigendom2)filter (?eigendom4!=?eigendom3)
  }
  bind ("3 eigenaren" as ?type)
  }



}
group by ?type
order by ?type
