   <{{buurt}}> imx:bevatPerceel ?perceel. 
      ?perceel imx:registratiegegevens ?preg.
      filter not exists {?preg <https://modellen.geostandaarden.nl/def/imx-geo#eindGeldigheid> []}


    ?perceel ^imx:bevindtZichOpPerceel ?uri.
      ?uri imx:registratiegegevens ?reg.
      filter not exists {?reg <https://modellen.geostandaarden.nl/def/imx-geo#eindGeldigheid> []}

    bind (strbefore(strafter(str(?uri),"/id/"),"/")  as ?color2)
    optional {?uri imx:type ?color3}
    bind (coalesce(?color3,?color2) as ?color)

    {?uri imx-ext:maaiveldgeometrie/geo:asWKT ?geometrymv. 
   optional {
  ?uri imx-ext:bovenaanzichtgeometrie/geo:asWKT ?geometrybo }
    bind (coalesce (?geometrybo,?geometrymv) as ?geometry)
  }

    union {

  ?uri geo:hasGeometry/geo:asWKT ?geometry }
  }



}


